        // Make SVG focusable to capture keyboard events
        document.querySelector('svg').setAttribute('tabindex', '0');

        // Current command being typed
        let currentCommand = "";
        let cursorPosition = 35; // Initial cursor position
        let commandHistory = [];
        let historyIndex = -1;

        // Command output data
        const commandOutputs = {
